// some utility functions																									/*																							    *//*-------------------------------------------t_std----------------------------------------------*//*			  																				    */#include "t_std.h"double midi2freq(double midiIn, double basePitch){    //m  =  12*log2(fm/440 Hz) + 69    // fm  =  2(mâˆ’69)/12(440 Hz).    return exp2((midiIn-69)/12) * basePitch;}double t_fmin(double *arr, int N){	int i;	double min;	min = arr[0];	if(N>1)	{		for(i=1;i<N;i++)		{			if(min>arr[i])				min = arr[i];		}	}	return min;}double t_fmax(double *arr, int N){	int i;	double max;	max = arr[0];	if(N>1)	{		for(i=1;i<N;i++)		{			if(max<arr[i])				max = arr[i];		}	}	return max;}double t_fsum(double *arr, int N){	int i;	double sum;	sum = arr[0];	if(N>1)	{		for(i=1;i<N;i++)		{			sum += arr[i];		}	}	return sum;}double t_fsign(double in){	double out;	if(in > 0)		out = 1;	else if(in == 0)		out = 0;	else		out = -1;		return out;}		 